	private static void printStartImage() {
		try {
			File file = Config.startImage();
			BufferedImage image = null;
			if (null != file) {
				image = ImageIO.read(file);
			} else {
				ByteArrayInputStream in = new ByteArrayInputStream(Base64.decodeBase64(DEFAULT_STARTIMAGE));
				image = ImageIO.read(in);
			}
			String ascii = new ImageToAcii().convert(image);
			System.out.println(ascii);
		} catch (Exception e) {
		}
	}

	public static final String DEFAULT_STARTIMAGE = "";

