package com.x.server.console;

import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.File;

import javax.imageio.ImageIO;

import org.apache.commons.codec.binary.Base64;
import org.apache.commons.lang3.BooleanUtils;
import org.apache.commons.lang3.StringUtils;
import org.apache.commons.lang3.SystemUtils;

import com.x.base.core.project.config.Config;
import com.x.base.core.project.logger.Logger;
import com.x.base.core.project.logger.LoggerFactory;
import com.x.base.core.project.tools.ImageToAcii;

public class CommandFactory {

	private CommandFactory() {
		// nothing
	}

	private static final Logger LOGGER = LoggerFactory.getLogger(CommandFactory.class);

	public static void printStartHelp() {
		try {
			printStartImage();
			LOGGER.print(">>> server directory:" + Config.base() + StringUtils.LF + ">>> version:" + Config.version()
					+ StringUtils.LF + ">>> java:" + SystemUtils.JAVA_VERSION + StringUtils.LF + ">>> os:"
					+ SystemUtils.OS_NAME + StringUtils.LF + ">>> nodeAgent "
					+ (BooleanUtils.isTrue(Config.currentNode().nodeAgentEnable())
							? ("port:" + Config.currentNode().nodeAgentPort() + ", encrypt:"
									+ Config.currentNode().nodeAgentEncrypt())
							: "disable"));
			printHelp();
		} catch (Exception e) {
			e.printStackTrace();
		}
	}

	public static void printHelp() {
		printHelpLine();
	}

	public static void printHelpLine() {
		try {
			String help = "";
			help += " help                                   show usage message.";
			help += StringUtils.LF;
			help += " start|stop [all]                       start stop all enable server.";
			help += StringUtils.LF;
			help += " start|stop data                        start stop data server.";
			help += StringUtils.LF;
			help += " start|stop storage                     start stop storage server.";
			help += StringUtils.LF;
			help += " start|stop center                      start stop center server.";
			help += StringUtils.LF;
			help += " start|stop application                 start stop application server.";
			help += StringUtils.LF;
			help += " start|stop web                         start stop web server.";
			help += StringUtils.LF;
			help += " start init                             start init server then start all enable server.";
			help += StringUtils.LF;
			help += " setPassword (oldpasswd) (newpasswd)    change initial manager password.";
			help += StringUtils.LF;
			help += " version                                display version.";
			help += StringUtils.LF;
			help += " exit                                   exit after stop all enable server.";
			help += StringUtils.LF;
			help += " ctl -<argument> option                 system control command, no argument display help.";
			help += StringUtils.LF;
			LOGGER.print(help);
		} catch (Exception e) {
			e.printStackTrace();
		}
	}

	private static void printStartImage() {
		try {
			File file = Config.startImage();
			BufferedImage image = null;
			if (null != file) {
				image = ImageIO.read(file);
			} else {
				ByteArrayInputStream in = new ByteArrayInputStream(Base64.decodeBase64(DEFAULT_STARTIMAGE));
				image = ImageIO.read(in);
			}
			String ascii = new ImageToAcii().convert(image);
			LOGGER.print(ascii);
		} catch (Exception e) {
			LOGGER.error(e);
		}
	}

	public static final String DEFAULT_STARTIMAGE = "";

}
