script.code:
var rt = "";
var idList = this.session.user.identityList;
var disList = [];
for(i=0;i<idList.length;i++){
    disList.push(idList[i].distinguishedName)
}

var action = MWF.Actions.get("x_organization_assemble_express");
action.listWithIdentity({identityList:disList},function(json){
    if(json.data.unitList){
        rt = json.data.unitList[0];
    }
}.bind(this),null,false)

return rt.split("@")[0];

