     var item = source.subSourceItems[this.data.picIndex];
     item.node.fade("out");
     if (this.data.picIndex-1<0){
         item = source.subSourceItems[source.subSourceItems.length-1];
         this.data.picIndex = source.subSourceItems.length-1;
     }else{
         item = source.subSourceItems[this.data.picIndex-1];
         this.data.picIndex--;
     }
     item.node.fade("in");
}
window.setTimeout(function(){
    this.beginTogglePic();
}.bind(this), 1000);

