blur.code:
var line = this.target.parentLine;  //获取当前行
var priceField = line.getModule("price"); //获取同一行的价格字段
var totalField = line.getModule("total"); //获取同一行的总计字段
if( priceField && totalField ){
    var count = this.target.getData(); //获取当前字段的值
    var price = priceField.getData();
    if( count && price && !isNaN(count) && !isNaN(price) ){
        var total = parseFloat(count)*parseFloat(price);
        totalField.setData(total)
    }
}

