                layout._loadCss = function(){
                    var key = encodeURIComponent(this.cssPath);
                    if (MWF.widget.css[key]){
                        this.css = MWF.widget.css[key];
                    }else{
                        var r = new Request.JSON({
                            url: this.cssPath,
                            secure: false,
                            async: false,
                            method: "get",
                            noCache: false,
                            onSuccess: function(responseJSON, responseText){
                                this.css = responseJSON;
                                MWF.widget.css[key] = responseJSON;
                            }.bind(this),
                            onError: function(text, error){
                                alert(error + text);
                            }
                        });
                        r.send();
                    }
                };
                layout.getIds = function(){

                    var href = window.location.href;
                    var qStr = href.substr(href.indexOf("?")+1, href.length);
                    var qDatas = qStr.split("&");
                    var obj = {};
                    qDatas.each(function(d){
                        var q = d.split("=");
                        obj[q[0].toLowerCase()] = q[1];
                    });

                    return obj;
                };

                layout.loadWork = function(options){
                    var method = "";
                    var id = "";
                    if (options.taskid){
                        method = "getTask";
                        id = options.taskid;
                    }else if (options.taskcompletedid) {
                        method = "getTaskCompleted";
                        id = options.taskcompletedid;
                    }else if (options.readid) {
                        method = "getRead";
                        id = options.readid;
                    }else if (options.readid) {
                        method = "getReaded";
                        id = options.readid;
                    }else if (options.reviewid) {
                        method = "getReview";
                        id = options.reviewid;
                    }
                    var workMethod = "";
                    var workid = "";
                    if (method && id){
                        this.action[method](id, function(json){
                            if (json.data.workCompleted){
                                workMethod = "getJobByWorkCompletedMobile";
                                workid = json.data.workCompleted
                            }else{
                                workMethod = "getJobByWorkMobile";
                                workid = json.data.work
                            }
                        }.bind(this), null, false);
                    }

                    if (!workMethod || !workid){
                        if (options.workid) {
                            workMethod = "getJobByWorkMobile";
                            workid = options.workid;
                        }else if (options.workcompletedid){
                            workMethod = "getJobByWorkCompletedMobile";
                            workid = options.workcompletedid;
                        }
                    }
                    if (workMethod && workid){
                        this.action[workMethod](function(json){
                            if (this.mask) this.mask.hide();
                            this.parseData(json.data);
                            this.openWork();
                        }.bind(this), function(xhr){
                            this.errorWork(xhr);
                        }.bind(this), workid);
                    }
                };

                layout.errorWork = function(xhr){
                    if (this.mask) this.mask.hide();
                    var json = JSON.decode(xhr.responseText);
                    if (json.prompt.indexOf("ExceptionWorkAccessDenied")!=-1){
                        if (this.authentication) this.authentication.loadLogin(this.node);
                    }else{
                        this.node.set("text", "openError");
                    }
                };
                layout.getCurrentTaskData = function(data){
                    if ((data.currentTaskIndex || data.currentTaskIndex===0) && data.currentTaskIndex != -1){
                        this.options.taskid = this.taskList[data.currentTaskIndex].id;
                        return this.taskList[data.currentTaskIndex];
                    }
                    //if (this.taskList){
                    //    if (this.taskList.length==1){
                    //        this.options.taskid = this.taskList[0].id;
                    //        return this.taskList[0];
                    //    }
                    //}
                    return null;
                };
                layout.parseData = function(data){
                    //   this.setTitle(this.options.title+"-"+data.work.title);

                    this.activity = data.activity;
                    this.data = data.data;
                    this.taskList = data.taskList;
                    this.currentTask = this.getCurrentTaskData(data);
                    this.taskList = data.taskList;
                    this.readList = data.readList;
                    this.work = data.work;
                    this.workCompleted = data.workCompleted;
                    this.workLogList = data.workLogList;
                    this.attachmentList = data.attachmentList;
                    this.inheritedAttachmentList = data.inheritedAttachmentList;
                    this.control = data.control;
                    this.form = (data.form) ? JSON.decode(MWF.decodeJsonString(data.form.data)): null;
                    this.formInfor = data.form;
                };
                layout.openWork = function(){
                    if (this.form){
                        MWF.xDesktop.requireApp("process.Xform", "Form", function(){
                            this.appForm = new MWF.APPForm(this.node, this.form, {"readonly": (this.currentTask) ? false : true});
                            this.appForm.businessData = {
                                "data": this.data,
                                "taskList": this.taskList,
                                "readList": this.readList,
                                "work": this.work,
                                "workCompleted": this.workCompleted,
                                "control": this.control,
                                "activity": this.activity,
                                "task": this.currentTask,
                                "workLogList": this.workLogList,
                                "attachmentList": this.attachmentList,
                                "inheritedAttachmentList": this.inheritedAttachmentList,
                                "formInfor": this.formInfor,
                                "status": {
                                    "readonly": (this.currentTask) ? false : true
                                }
                            };
                            this.appForm.workAction = this.action;
                            this.appForm.app = this;
                            this.appForm.load();

                            if (window.o2android && window.o2android.appFormLoaded){
                                window.o2android.appFormLoaded('true');
                            }
                            if (window.webkit && window.webkit.messageHandlers && window.webkit.messageHandlers.appFormLoaded){
                                console.log("begin run appFormLoaded ios");
                                window.webkit.messageHandlers.appFormLoaded.postMessage('true');
                                console.log("end run appFormLoaded ios");
                            }
                        }.bind(this));
                    }
                };
