                    // && mwftype !== "tab$Content"
                    if (mwftype !== "datagrid" && mwftype !== "datatable" && mwftype !== "subSource" && mwftype !== "tab$Content" && mwftype !== "datatemplate") {
                        moduleNodes = moduleNodes.concat(this._getModuleNodes(subDom, dollarFlag));
                    }
