            var flag = this.Macro.exec(this.json.validationRoute.code, this);
            if (!flag) flag = MWF.xApplication.process.Xform.LP.notValidation;
            if (flag.toString() != "true") {
                this.notValidationRouteMode(flag, processor);
                return false;
            }
            return true;
